import { useState } from 'react'
import classNames from 'classnames'
import ShowMoreButton from './ShowMoreButton'

export default function Details({ details }) {
  const [showFullDetails, setShowFullDetails] = useState(false)

  return (
    <div>
      <div
        className={classNames('mb-5 overflow-hidden', {
          'max-h-52': !showFullDetails,
        })}
      >
        {details.map((detail) => {
          return (
            <div className="mb-6">
              <h3 className="font-bold">{detail.label}:</h3>
              <p>{detail.value}</p>
            </div>
          )
        })}
      </div>
      <ShowMoreButton
        showMore={showFullDetails}
        handleClick={setShowFullDetails}
        showMoreLabel="Show Full Description"
        showLessLabel="Hide Full Description"
      />
    </div>
  )
}
